// eslint-disable-next-line vue/multi-word-component-names
<template>
    <div class="countdown">
      <div class="countdown-item">
        <span class="countdown-value">{{ days }}</span>
        <span class="countdown-title">Days</span>
      </div>
      <div class="countdown-item">
        <span class="countdown-value">{{ hours }}</span>
        <span class="countdown-title">Hours</span>
      </div>
      <div class="countdown-item">
        <span class="countdown-value">{{ minutes }}</span>
        <span class="countdown-title">Minutes</span>
      </div>
      <div class="countdown-item">
        <span class="countdown-value">{{ seconds }}</span>
        <span class="countdown-title">Seconds</span>
      </div>
    </div>
  </template>

  <script>
  export default {
    // eslint-disable-next-line vue/multi-word-component-names
    name: 'Countdown',
    data() {
      return {
        targetDate: new Date("2024-05-01T14:30:59"), // Set your target date and time
        days: 0,
        hours: 0,
        minutes: 0,
        seconds: 0,
      };
    },
    mounted() {
      this.calculateTime();
      setInterval(this.calculateTime, 1000);
    },
    methods: {
      calculateTime() {
        const now = new Date();
        const difference = this.targetDate - now;

        this.days = Math.floor(difference / (1000 * 60 * 60 * 24));
        this.hours = Math.floor((difference % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
        this.minutes = Math.floor((difference % (1000 * 60 * 60)) / (1000 * 60));
        this.seconds = Math.floor((difference % (1000 * 60)) / 1000);
      },
    },
  };
  </script>

  <style scoped>
  .countdown {
    display: flex;
    justify-content: space-around;
    font-family: 'Arial', sans-serif;
  }

  .countdown-item {
    text-align: center;
    color: #673147;
  }

  .countdown-value {
    font-size: 1.5rem;
    font-weight: bold;
  }

  .countdown-title {
    font-size: 0.75rem;
  }
  </style>
